mapper.update:
UPDATE tb_recruit
SET recruit_count = recruit_count + 1
WHERE recruit_no = #{rno}

mapper.select:
SELECT
    COUNT(*)
FROM tb_member_recruit
WHERE
    member_id = #{memberId}
AND recruit_no = #{rno}

mapper.update:
UPDATE tb_recruit
SET recruit_count = recruit_count - 1
WHERE recruit_no = #{rno}

mapper.delete:
DELETE
FROM
    tb_member_recruit
WHERE
    recruit_no = #{rno}
AND member_id = #{memberId}

mapper.select:
select
    *
from
    tb_member_recruit
WHERE member_id = #{memberId}
AND recruit_no = #{rno}
